import React, { Fragment } from 'react';
import {  activateMail } from '../../actions/auth';
import { connect } from 'react-redux';
const MailActivation = () => {
  // const token=window.location.pathname;
  let token = window.location.href.split('/')
  // let base64Url = token.split('.')[1]; // token you get
  // let base64 = base64Url.replace('-', '+').replace('_', '/');
  // let decodedData = JSON.parse(Buffer.from(base64, 'base64').toString('binary'));
  // let decodedData=jwt.decode(token);
  // let {name,email, password}=decodedData
  // console.log(name+email+password)
  const onSubmit = async (e) => {
    e.preventDefault();
    activateMail(token[5]);
    // window.localStorage.setItem('token', token[5]);
    // console.log(token);
    console.log(token[5]);
    // console.log(decodedData);
  };
  return (
    <Fragment>
      <h2 className='text-primary'>
        You are almost done.
        To Activate Your Account on Me2Me Please Click on the Activate Account !
      </h2>
      <form
        className='form'
        onSubmit={(e) => onSubmit(e)}
      >
        <input
          type='submit'
          className='btn btn-primary'
          value='Activate Account'
        />
      </form>
    </Fragment>
  );
};
MailActivation.propTypes = {
  activateMail: PropTypes.func.isRequired,
};
export default connect(null, { activateMail })(MailActivation);
