import React, { Fragment } from 'react';
import {  activateMail } from '../../actions/auth';
import {  Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
const MailActivation = ({isAuthenticated}) => {
  // const token=window.location.pathname;
  let urlElements = window.location.href.split('/');
  let token='';
  token=urlElements[5];
  const body1 = JSON.stringify({token});
  
  window.localStorage.setItem('token', token);
  const onSubmit = async (e) => {
    e.preventDefault();
    activateMail(token);
    console.log(urlElements);
    console.log(token);
    console.log(body1);
    // console.log(decodedData);
  };
  // if (isAuthenticated) {
  //   return <Redirect to='/login' />;
  // }
  return (
    <Fragment>
      <h2 className='text-primary'>
        You are almost done.
        To Activate Your Account on Me2Me Please Click on the Activate Account !
      </h2>
      <form
        className='form'
        onSubmit={(e) => onSubmit(e)}
      >
        <input
          type='submit'
          className='btn btn-primary'
          value='Activate Account'
        />
      </form>
    </Fragment>
  );
};
MailActivation.propTypes = {
  activateMail: PropTypes.func.isRequired,
};
export default connect(null, { activateMail })(MailActivation);
