import React, { Fragment} from 'react';
// import axios from 'axios';
import {Link} from 'react-router-dom';


const MailActivation = ({ setAlert, register, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
  });
  const { name, email, password, password2 } = formData;
  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });
  const onSubmit = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      setAlert('Password do not match', 'danger');
    } else {
      // console.log('Successfully registered', formData);
      register({ name, email, password });
    }
  };
  // Redirect if Registered
  if (isAuthenticated) {
    return <Redirect to='/dashboard' />;
  }
  return (
    <Fragment>
      <h1 className='large text-primary'>You have been succesfuly activated. You can login now!{' '}</h1>
      <p className='lead'>
      <Link to="/login">
          <a>
            Login
          </a>
        </Link>
      </p>
    </Fragment>
  );
};
export default MailActivation;
