import React, { Fragment } from 'react';
import {  activateMail } from '../../actions/auth';
import {  Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
const MailActivation = ({isAuthenticated}) => {
  // const token=window.location.pathname;
  let urlElements = window.location.href.split('/');
  let token=urlElements[5];
  const obj={
    token1:''
  }
  token
  // let base64Url = token.split('.')[1]; // token you get
  // let base64 = base64Url.replace('-', '+').replace('_', '/');
  // let decodedData = JSON.parse(Buffer.from(base64, 'base64').toString('binary'));
  // let decodedData=jwt.decode(token);
  // let {name,email, password}=decodedData
  // console.log(name+email+password)
  const onSubmit = async (e) => {
    e.preventDefault();
    activateMail(token);
    // window.localStorage.setItem('token', token[5]);
    console.log(urlElements);
    console.log(token);
    // console.log(decodedData);
  };
  if (isAuthenticated) {
    return <Redirect to='/login' />;
  }
  return (
    <Fragment>
      <h2 className='text-primary'>
        You are almost done.
        To Activate Your Account on Me2Me Please Click on the Activate Account !
      </h2>
      <form
        className='form'
        onSubmit={(e) => onSubmit(e)}
      >
        <input
          type='submit'
          className='btn btn-primary'
          value='Activate Account'
        />
      </form>
    </Fragment>
  );
};
MailActivation.propTypes = {
  activateMail: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated,
});
export default connect(mapStateToProps, { activateMail })(MailActivation);
