import React, { Fragment, useState } from 'react';
// import axios from 'axios';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { setAlert } from '../../actions/alert';
import { activateMail } from '../../actions/auth';
import PropTypes from 'prop-types';


const MailActivation = ({ setAlert,activateMail, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
  });
  const { name, email, password, password2 } = formData;
  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });
  const onSubmit = async (e) => {
    e.preventDefault();
    if (password !== password2) {
      setAlert('Password do not match', 'danger');
    } else {
      // console.log('Successfully registered', formData);
    activateMail({ name, email, password });
    }
  };
  // Redirect if Registered
  // if (isAuthenticated) {
  //   return <Redirect to='/login' />;
  // }
  return (
    <Fragment>
      <h1 className='large text-primary'>You have been succesfuly activated. You can login now!{' '}</h1>
      <p className='lead'>
      <Link to="/login">
          <a>
            Login
          </a>
        </Link>
      </p>
    </Fragment>
  );
};
MailActivation.propTypes = {
  setAlert: PropTypes.func.isRequired,
activateMail: PropTypes.func.isRequired,
  // isAuthenticated: PropTypes.bool,
};
// const mapStateToProps = (state) => ({
//   isAuthenticated: state.auth.isAuthenticated,
// });
export default connect(null, { setAlert,activateMail })(MailActivation);

